<script setup lang="ts">
const scrollIcon: any = ref('');
const scrollIconText: any = ref('');

const rotate = () => {
  scrollIconText.value.style.transform = 'rotate(' + (window.scrollY * 0.15) + 'deg)';
};

onMounted(() => {
  rotate();
  scrollIcon.value.classList.add('_active');
  window.addEventListener('scroll', rotate);
});
</script>

<style scoped lang="scss">
.scroll__icon {
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  border-radius: 50%;
  background: var(--fg-blue);
  z-index: 998;
  width: 9.1rem;
  height: 9.1rem;

  display: flex;
  align-items: center;
  justify-content: center;

  opacity: 0;
  transition: transform .2s;
  transition-delay: .5s;

  &._active {
    opacity: 1;
  }

  @media (max-width: $tablet) {
    display: none;
  }

  &--white {
    background: var(--fg-white);
    color: var(--fg-blue);

    & path {
      fill: var(--fg-blue);
    }
  }
}

.scroll__icon-text {
  width: 100%;
  height: 100%;

  display: flex;
  align-items: center;
  justify-content: center;

  transform-origin: center center;
  transition: transform .5s ease-out;
}

.scroll__icon-logo {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}
</style>